import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { Card } from './Card';
import { CardContent } from './CardContent';
import { Button } from './Button';
import { Select } from './Select';
import axios from 'axios';

const financialMetrics = {
  assets: "Assets",
  liabilities: "Liabilities",
  equity: "Equity",
};

const FinVueAnalysis = () => {
  const [selectedData, setSelectedData] = useState('assets');
  const [metricsData, setMetricsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [companyList, setCompanyList] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        const companyTickersResponse = await axios.get(
          "http://localhost:5001/api/company-tickers"
        );
        const companyDataArray = Object.values(companyTickersResponse.data);
        setCompanyList(companyDataArray);
        setSelectedCompany(companyDataArray[0]);
      } catch (error) {
        console.error("Error fetching companies:", error);
        setError("An error occurred while fetching company data. Please try again later.");
      }
    };

    fetchCompanies();
  }, []);

  useEffect(() => {
    if (selectedCompany) {
      const fetchData = async () => {
        try {
          setLoading(true);
          const cik = selectedCompany.cik_str.toString().padStart(10, '0');

          const companyConceptResponse = await axios.get(
            `http://localhost:5001/api/company-concept/${cik}/${financialMetrics[selectedData]}`
          );

          const data = companyConceptResponse.data.units.USD;
          const filteredData = data.filter(item => item.form === '10-Q' || item.form === '10-K')
            .sort((a, b) => new Date(a.end) - new Date(b.end));

          setMetricsData(filteredData);
          setLoading(false);
        } catch (error) {
          console.error("Error fetching data:", error);
          setError("An error occurred while fetching financial data. Please try again later.");
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [selectedCompany, selectedData]);

  const renderChart = (data) => (
    <ResponsiveContainer width="100%" height={300}>
      <BarChart data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="end" tickFormatter={(date) => new Date(date).toLocaleDateString()} />
        <YAxis />
        <Tooltip
          labelFormatter={(date) => new Date(date).toLocaleDateString()}
          formatter={(value) => `$${value.toLocaleString()}`}
        />
        <Bar dataKey="val" fill="#8884d8" />
      </BarChart>
    </ResponsiveContainer>
  );

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="p-4">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Stock Analysis: {selectedCompany?.title}</h1>
        <Select
          value={selectedCompany?.ticker}
          onChange={(e) => setSelectedCompany(companyList.find(c => c.ticker === e.target.value))}
        >
          {companyList.map(company => (
            <option key={company.ticker} value={company.ticker}>
              {company.title}
            </option>
          ))}
        </Select>
      </header>

      <Card>
        <CardContent>
          <h2 className="text-xl font-semibold mb-4">Data Overview</h2>
          <div className="flex space-x-2 mb-4">
            {Object.keys(financialMetrics).map(metric => (
              <Button
                key={metric}
                variant={selectedData === metric ? 'default' : 'outline'}
                onClick={() => setSelectedData(metric)}
              >
                {financialMetrics[metric]}
              </Button>
            ))}
          </div>
          {renderChart(metricsData)}
          <div className="text-center mt-4">
            <span className="text-sm text-gray-500">
              Avg. {financialMetrics[selectedData]}: ${(metricsData.reduce((sum, item) => sum + item.val, 0) / metricsData.length).toLocaleString()}
            </span>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default FinVueAnalysis;
