import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import { Card } from './Card';
import { CardContent } from './CardContent';
import { Button } from './Button';
import { Select } from './Select';
import axios from 'axios';
import pLimit from 'p-limit';
import { DollarSign, TrendingUp, Users, PieChart, Activity } from 'lucide-react';

const financialMetrics = {
  assets: { name: "Assets", icon: <DollarSign /> },
  liabilities: { name: "Liabilities", icon: <TrendingUp /> },
  equity: { name: "Stockholders Equity", icon: <Users /> },
  revenue: { name: "Revenues", icon: <PieChart /> },
  netIncome: { name: "Net Income/Loss", icon: <Activity /> },
};

const limit = pLimit(5);

const FinVueAnalysis = () => {
  const [selectedMetric, setSelectedMetric] = useState('assets');
  const [metricsData, setMetricsData] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [companyList, setCompanyList] = useState([]);
  const [selectedCompanies, setSelectedCompanies] = useState([]);
  const [isComparing, setIsComparing] = useState(false);
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        setLoading(true);
        const companyTickersResponse = await axios.get(
          "http://localhost:5001/api/company-tickers"
        );
        const companyDataArray = Object.values(companyTickersResponse.data);
        setCompanyList(companyDataArray);
        setSelectedCompanies([companyDataArray[0]]);
      } catch (error) {
        console.error("Error fetching companies:", error);
        setError("An error occurred while fetching company data. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchCompanies();
    fetchNews();
  }, []);

  useEffect(() => {
    if (selectedCompanies.length > 0) {
      fetchData();
    }
  }, [selectedCompanies]);

  const fetchData = async () => {
    // ... (keep the existing fetchData function)
  };

  const fetchNews = async () => {
    try {
      const response = await axios.get(
        'https://api.marketaux.com/v1/news/all',
        {
          params: {
            countries: 'us',
            filter_entities: true,
            limit: 10,
            published_after: '2024-07-29T21:56',
            api_token: 'eM6ilsiHpSR7PiBPONMooaKNeP3Ks7WzSKYYbfKy'
          }
        }
      );
      setNews(response.data.data);
    } catch (error) {
      console.error("Error fetching news:", error);
    }
  };

  const renderFinancialOverview = () => (
    <Card className="financial-overview">
      <CardContent>
        <h2>Financial Overview</h2>
        <div className="financial-grid">
          {Object.entries(financialMetrics).map(([key, { name, icon }]) => (
            <div key={key} className="financial-item">
              <h3>{icon} {name}</h3>
              {selectedCompanies.map(company => (
                <p key={company.ticker} className="financial-value">
                  {company.title}: 
                  {metricsData[company.ticker] && 
                   metricsData[company.ticker][key] &&
                   metricsData[company.ticker][key].tenKData &&
                   metricsData[company.ticker][key].tenKData.length > 0
                    ? `$${metricsData[company.ticker][key].tenKData[metricsData[company.ticker][key].tenKData.length - 1].val.toLocaleString()}`
                    : 'N/A'}
                </p>
              ))}
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );

  const renderCharts = () => (
    <Card className="financial-charts">
      <CardContent>
        <h2>{financialMetrics[selectedMetric].name} Trends</h2>
        <div className="chart-controls">
          {Object.entries(financialMetrics).map(([key, { name }]) => (
            <Button
              key={key}
              onClick={() => setSelectedMetric(key)}
              className={selectedMetric === key ? 'active' : ''}
            >
              {name}
            </Button>
          ))}
        </div>
        <div className="chart-container">
          <h3>Annual Data (10-K)</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={selectedCompanies.flatMap(company => 
              (metricsData[company.ticker]?.[selectedMetric]?.tenKData || []).map(item => ({
                ...item,
                company: company.title
              }))
            )}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="end" tickFormatter={(date) => new Date(date).getFullYear()} />
              <YAxis />
              <Tooltip
                labelFormatter={(date) => new Date(date).toLocaleDateString()}
                formatter={(value) => `$${value.toLocaleString()}`}
              />
              <Legend />
              <Bar dataKey="val" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </div>
        <div className="chart-container">
          <h3>Quarterly Data (10-Q)</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={selectedCompanies.flatMap(company => 
              (metricsData[company.ticker]?.[selectedMetric]?.tenQData || []).map(item => ({
                ...item,
                company: company.title
              }))
            )}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="end" tickFormatter={(date) => new Date(date).toLocaleDateString()} />
              <YAxis />
              <Tooltip
                labelFormatter={(date) => new Date(date).toLocaleDateString()}
                formatter={(value) => `$${value.toLocaleString()}`}
              />
              <Legend />
              <Line type="monotone" dataKey="val" stroke="#8884d8" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  );

  const handleCompanyChange = (e) => {
    const selectedCompany = companyList.find(c => c.ticker === e.target.value);
    if (isComparing) {
      setSelectedCompanies(prev => [...prev, selectedCompany]);
    } else {
      setSelectedCompanies([selectedCompany]);
    }
  };

  const toggleComparison = () => {
    setIsComparing(!isComparing);
    if (!isComparing) {
      setSelectedCompanies(prev => [prev[0]]);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="finvue-analysis">
      <aside className="sidebar">
        <h1>FinVue</h1>
        <nav>
          <ul>
            <li className="active">Dashboard</li>
            <li>Filings</li>
            <li>Live News</li>
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <header className="main-header">
          <Select
            value={selectedCompanies[selectedCompanies.length - 1]?.ticker}
            onChange={handleCompanyChange}
          >
            {companyList.map(company => (
              <option key={company.ticker} value={company.ticker}>
                {company.title}
              </option>
            ))}
          </Select>
          <Button onClick={toggleComparison} className="compare-button">
            {isComparing ? 'üîç Stop Comparing' : 'üîç Compare Companies'}
          </Button>
          <div className="header-icons">
            <span className="icon">üîç</span>
            <span className="icon">üîî</span>
            <span className="icon">üë§</span>
          </div>
        </header>
        <div className="dashboard-grid">
          {renderFinancialOverview()}
          {renderCharts()}
        </div>
        <Card className="live-news">
          <CardContent>
            <h2>Live News</h2>
            <ul>
              {news.map((item, index) => (
                <li key={index}>
                  <a href={item.url} target="_blank" rel="noopener noreferrer">{item.title}</a>
                </li>
              ))}
            </ul>
          </CardContent>
        </Card>
      </main>
    </div>
  );
};

export default FinVueAnalysis;
