import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import { DollarSign, TrendingUp, Users, PieChart, Search, Bell, User, Home, FileText, Settings, LogOut } from 'lucide-react';
import axios from 'axios';
import pLimit from 'p-limit';
import './FinVueAnalysis.css'; 

const financialMetrics = {
  assets: { name: "Assets", icon: DollarSign },
  liabilities: { name: "Liabilities", icon: TrendingUp },
  equity: { name: "StockholdersEquity", icon: Users },
  revenue: { name: "Revenues", icon: PieChart },
};

const limit = pLimit(5);

const FinVueAnalysis = () => {
  const [selectedMetric, setSelectedMetric] = useState('assets');
  const [metricsData, setMetricsData] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [companyList, setCompanyList] = useState([]);
  const [selectedCompanies, setSelectedCompanies] = useState([]);
  const [isComparing, setIsComparing] = useState(false);
  const [showNews, setShowNews] = useState(false);
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        setLoading(true);
        const companyTickersResponse = await axios.get(
          "http://localhost:5001/api/company-tickers"
        );
        const companyDataArray = Object.values(companyTickersResponse.data);
        setCompanyList(companyDataArray);
        setSelectedCompanies([companyDataArray[0]]);
      } catch (error) {
        console.error("Error fetching companies:", error);
        setError("An error occurred while fetching company data. Please try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchCompanies();
    fetchNews();
  }, []);

  useEffect(() => {
    if (selectedCompanies.length > 0) {
      fetchData();
    }
  }, [selectedCompanies]);

  const fetchData = async () => {
    try {
      setLoading(true);
      const newMetricsData = {};

      const fetchPromises = selectedCompanies.flatMap(company => {
        const cik = company.cik_str.toString().padStart(10, '0');
        newMetricsData[company.ticker] = {};

        return Object.entries(financialMetrics).map(([key, value]) =>
          limit(async () => {
            try {
              console.log(`Fetching data for ${company.ticker}, metric: ${value.name}`);
              const response = await axios.get(
                `http://localhost:5001/api/company-concept/${cik}/${encodeURIComponent(value.name)}`
              );

              const data = response.data.units.USD;
              const tenKData = data.filter(item => item.form === '10-K').sort((a, b) => new Date(a.end) - new Date(b.end));
              const tenQData = data.filter(item => item.form === '10-Q').sort((a, b) => new Date(a.end) - new Date(b.end));

              newMetricsData[company.ticker][key] = { tenKData, tenQData };
            } catch (error) {
              console.error(`Error fetching data for ${company.ticker}, metric: ${value.name}`, error.message);
              newMetricsData[company.ticker][key] = { tenKData: [], tenQData: [] };
            }
          })
        );
      });

      await Promise.all(fetchPromises);
      setMetricsData(newMetricsData);
    } catch (error) {
      console.error("Error in fetchData:", error);
      setError("An error occurred while fetching financial data. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const fetchNews = async () => {
    try {
      const response = await axios.get('https://api.marketaux.com/v1/news/all', {
        params: {
          countries: 'us',
          filter_entities: true,
          limit: 10,
          published_after: '2024-07-29T21:56',
          api_token: 'eM6ilsiHpSR7PiBPONMooaKNeP3Ks7WzSKYYbfKy'
        }
      });
      setNews(response.data.data);
    } catch (error) {
      console.error("Error fetching news:", error);
    }
  };

  const renderSidebar = () => (
    <aside className="sidebar">
      <div className="logo">FinVue</div>
      <nav>
        <ul>
          <li className="active"><Home size={18} /> Dashboard</li>
          <li><FileText size={18} /> Reports</li>
          <li><Settings size={18} /> Settings</li>
        </ul>
      </nav>
      <div className="logout">
        <LogOut size={18} /> Logout
      </div>
    </aside>
  );

  const renderTopBar = () => (
    <header className="top-bar">
      <div className="search-bar">
        <Search size={20} />
        <input type="text" placeholder="Search..." />
      </div>
      <div className="right-icons">
        <div className="bell-icon" onClick={() => setShowNews(!showNews)}>
          <Bell size={20} />
        </div>
        <div className="user-icon">
          <User size={20} />
        </div>
      </div>
    </header>
  );

  const renderSummaryCards = () => (
    <div className="summary-cards">
      {Object.entries(financialMetrics).map(([key, { name, icon: Icon }]) => (
        <div key={key} className="summary-card">
          <Icon size={24} />
          <div className="summary-content">
            <h3>{name}</h3>
            <p>{metricsData[selectedCompanies[0]?.ticker]?.[key]?.tenKData?.slice(-1)[0]?.val.toLocaleString() || 'N/A'}</p>
          </div>
        </div>
      ))}
    </div>
  );

  const renderFinancialOverview = () => (
    <div className="card financial-overview">
      <h2 className="card-title">Financial Overview</h2>
      <div className="financial-grid">
        {Object.entries(financialMetrics).map(([key, { name, icon: Icon }]) => (
          <div key={key} className="financial-item">
            <h3><Icon size={18} /> {name}</h3>
            {selectedCompanies.map(company => (
              <p key={company.ticker} className="financial-value">
                {company.title}: 
                {metricsData[company.ticker] && 
                 metricsData[company.ticker][key] &&
                 metricsData[company.ticker][key].tenKData &&
                 metricsData[company.ticker][key].tenKData.length > 0
                  ? `$${metricsData[company.ticker][key].tenKData[metricsData[company.ticker][key].tenKData.length - 1].val.toLocaleString()}`
                  : 'N/A'}
              </p>
            ))}
          </div>
        ))}
      </div>
    </div>
  );

  const renderCharts = () => (
    <div className="card financial-charts">
      <h2 className="card-title">{financialMetrics[selectedMetric].name} Trends</h2>
      <div className="chart-controls">
        {Object.entries(financialMetrics).map(([key, { name }]) => (
          <button
            key={key}
            onClick={() => setSelectedMetric(key)}
            className={selectedMetric === key ? 'active' : ''}
          >
            {name}
          </button>
        ))}
      </div>
      <div className="chart-container">
        <h3>Annual Data (10-K)</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={selectedCompanies.flatMap(company => 
            (metricsData[company.ticker]?.[selectedMetric]?.tenKData || []).map(item => ({
              ...item,
              company: company.title
            }))
          )}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="end" tickFormatter={(date) => new Date(date).getFullYear()} />
            <YAxis />
            <Tooltip
              labelFormatter={(date) => new Date(date).toLocaleDateString()}
              formatter={(value) => `$${value.toLocaleString()}`}
            />
            <Legend />
            <Bar dataKey="val" fill="#4a5568" />
          </BarChart>
        </ResponsiveContainer>
      </div>
      <div className="chart-container">
        <h3>Quarterly Data (10-Q)</h3>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={selectedCompanies.flatMap(company => 
            (metricsData[company.ticker]?.[selectedMetric]?.tenQData || []).map(item => ({
              ...item,
              company: company.title
            }))
          )}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="end" tickFormatter={(date) => new Date(date).toLocaleDateString()} />
            <YAxis />
            <Tooltip
              labelFormatter={(date) => new Date(date).toLocaleDateString()}
              formatter={(value) => `$${value.toLocaleString()}`}
            />
            <Legend />
            <Line type="monotone" dataKey="val" stroke="#4a5568" />
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );

  const renderNews = () => (
    <div className={`news-dropdown ${showNews ? 'show' : ''}`}>
      <h3>Latest News</h3>
      <ul>
        {news.slice(0, 5).map((item, index) => (
          <li key={index}>
            <a href={item.url} target="_blank" rel="noopener noreferrer">{item.title}</a>
          </li>
        ))}
      </ul>
      <a href="#" className="view-all">View All News</a>
    </div>
  );

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="finvue-analysis">
      {renderSidebar()}
      <main className="main-content">
        {renderTopBar()}
        <div className="dashboard-content">
          {renderSummaryCards()}
          <div className="data-grid">
            {renderFinancialOverview()}
            {renderCharts()}
          </div>
        </div>
      </main>
      {renderNews()}
    </div>
  );
};

export default FinVueAnalysis;
