import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { Card } from './Card';
import { CardContent } from './CardContent';
import { Button } from './Button';
import { Select } from './Select';
import axios from 'axios';
import './FinVueAnalysis.css';

const financialMetrics = {
  assets: "Assets",
  liabilities: "Liabilities",
  equity: "Stockholders' Equity",
  revenue: "Revenue",
  netIncome: "Net Income",
};

const organizeData = (data) => {
  const tenKData = data.filter(item => item.form === '10-K').sort((a, b) => new Date(a.end) - new Date(b.end));
  const tenQData = data.filter(item => item.form === '10-Q').sort((a, b) => new Date(a.end) - new Date(b.end));
  return { tenKData, tenQData };
};

const FinVueAnalysis = () => {
  const [selectedMetric, setSelectedMetric] = useState('assets');
  const [metricsData, setMetricsData] = useState({ tenKData: [], tenQData: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [companyList, setCompanyList] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        const companyTickersResponse = await axios.get(
          "http://localhost:5001/api/company-tickers"
        );
        const companyDataArray = Object.values(companyTickersResponse.data);
        setCompanyList(companyDataArray);
        setSelectedCompany(companyDataArray[0]);
      } catch (error) {
        console.error("Error fetching companies:", error);
        setError("An error occurred while fetching company data. Please try again later.");
      }
    };

    fetchCompanies();
  }, []);

  useEffect(() => {
    if (selectedCompany) {
      const fetchData = async () => {
        try {
          setLoading(true);
          const cik = selectedCompany.cik_str.toString().padStart(10, '0');

          const companyConceptResponse = await axios.get(
            `http://localhost:5001/api/company-concept/${cik}/${financialMetrics[selectedMetric]}`
          );

          const data = companyConceptResponse.data.units.USD;
          const { tenKData, tenQData } = organizeData(data);

          setMetricsData({ tenKData, tenQData });
          setLoading(false);
        } catch (error) {
          console.error("Error fetching data:", error);
          setError("An error occurred while fetching financial data. Please try again later.");
          setLoading(false);
        }
      };

      fetchData();
    }
  }, [selectedCompany, selectedMetric]);

  const renderFinancialOverview = () => (
    <Card className="financial-overview">
      <CardContent>
        <h2>Financial Overview</h2>
        <div className="financial-grid">
          {Object.entries(financialMetrics).map(([key, value]) => (
            <div key={key} className="financial-item">
              <h3>{value}</h3>
              <p className="financial-value">
                ${metricsData.tenKData.length > 0 
                  ? metricsData.tenKData[metricsData.tenKData.length - 1].val.toLocaleString() 
                  : 'N/A'}
              </p>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );

  const renderCharts = () => (
    <Card className="financial-charts">
      <CardContent>
        <h2>{financialMetrics[selectedMetric]} Trends</h2>
        <div className="chart-controls">
          {Object.entries(financialMetrics).map(([key, value]) => (
            <Button
              key={key}
              onClick={() => setSelectedMetric(key)}
              className={selectedMetric === key ? 'active' : ''}
            >
              {value}
            </Button>
          ))}
        </div>
        <div className="chart-container">
          <h3>Annual Data (10-K)</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={metricsData.tenKData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="end" tickFormatter={(date) => new Date(date).getFullYear()} />
              <YAxis />
              <Tooltip 
                labelFormatter={(date) => new Date(date).toLocaleDateString()}
                formatter={(value) => `$${value.toLocaleString()}`}
              />
              <Bar dataKey="val" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </div>
        <div className="chart-container">
          <h3>Quarterly Data (10-Q)</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={metricsData.tenQData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="end" tickFormatter={(date) => new Date(date).toLocaleDateString()} />
              <YAxis />
              <Tooltip 
                labelFormatter={(date) => new Date(date).toLocaleDateString()}
                formatter={(value) => `$${value.toLocaleString()}`}
              />
              <Line type="monotone" dataKey="val" stroke="#82ca9d" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </CardContent>
    </Card>
  );

  const renderFilingList = () => (
    <Card className="filing-list">
      <CardContent>
        <h2>Recent SEC Filings</h2>
        <ul>
          {metricsData.tenKData.concat(metricsData.tenQData)
            .sort((a, b) => new Date(b.end) - new Date(a.end))
            .slice(0, 5)
            .map((filing, index) => (
              <li key={index}>
                <span className="filing-date">{new Date(filing.end).toLocaleDateString()}</span>
                <span className="filing-type">{filing.form}</span>
                <Button className="view-btn">View</Button>
              </li>
            ))}
        </ul>
      </CardContent>
    </Card>
  );

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="finvue-analysis">
      <aside className="sidebar">
        <h1>FinVue</h1>
        <nav>
          <ul>
            <li className="active">Dashboard</li>
            <li>Company Search</li>
            <li>Comparison</li>
            <li>Filings</li>
            <li>Analysis</li>
            <li>Settings</li>
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <header className="main-header">
          <Select
            value={selectedCompany?.ticker}
            onChange={(e) => setSelectedCompany(companyList.find(c => c.ticker === e.target.value))}
          >
            {companyList.map(company => (
              <option key={company.ticker} value={company.ticker}>
                {company.title}
              </option>
            ))}
          </Select>
          <div className="header-icons">
            <span className="icon">üîç</span>
            <span className="icon">üîî</span>
            <span className="icon">üë§</span>
          </div>
        </header>
        <div className="dashboard-grid">
          {renderFinancialOverview()}
          {renderCharts()}
          {renderFilingList()}
        </div>
      </main>
    </div>
  );
};

export default FinVueAnalysis;
